import React from 'react';
import { Link } from 'react-router5';
import {connect} from 'react-redux';
import {ROUTES} from '../../constants/router.consts';
import axios from "axios";
import './style.css';

class Workers extends React.Component {
    constructor(props) {
       super(props)
       this.state = {
        workers: {
        zones: {
          undef: {
            number: 0,
            name: "Вне зон"
          },
        },
        total: 0,
        violations: []
        }
      };
    }
    componentDidMount() {
      let url = "camera/1/workers/number/curent/";
        axios.get(url).then(res => {
            this.setState({workers: res.data.workers});
        });
    }
    render() {
        let dataworker = this.props.dataworker;
        let total = 0;
        if (dataworker && dataworker.total) {
            total = dataworker.total;
        }
        var zones;
        if (dataworker && dataworker.zones) {
            zones = Object.entries(dataworker.zones).map((mapdata, i) => {
                let data = mapdata[1];
                return (
                    <tr>
                        <td>{data.name}</td>
                        <td>{data.number}</td>
                    </tr>
                );
            });
        }
        return (
          <div className="menu-wrapper">
            <div className="submenu-wrapper">
              <table className="table table-hover table-bordered">
                <thead>
                    <tr>
                        <th>Зона</th>
                        <th>Колличество персонала</th>
                    </tr>
                </thead>
                <tbody>
                    {zones}
                    <tr>
                        <td>Все</td>
                        <td>
                            <span className="player-number-workers">
                                {total}
                            </span>
                        </td>
                    </tr>
                </tbody>
              </table>
            </div>
          </div>
        );
    }
}
export default Workers;
